import React, { useState, useEffect } from 'react'
import axios from "axios"
function Home() {
    const [moviename, setMoviename] = useState("");
    const [genre, setGenre] = useState("");
    const [rating, setRating] = useState("");
    const [release, setRelease] = useState("");
    const [director, setDirector] = useState("");
    const [country, setCountry] = useState("");
    const [image, setImage] = useState("")
    const [movielist, setMovielist] = useState([])


    useEffect(() => {
        axios.get('/api/movies').then((res)=>{
            setMovielist(res.data)
            console.log(movielist)
        })
    }, [])
    

    const onChangeImage = (e) => {
        setImage(e.target.files[0]);
    }

    const changeOnClick = (e) => {
        e.preventDefault();

        const formData = new FormData();

        formData.append("moviename", moviename);
        formData.append("genre", genre);
        formData.append("rating", rating);
        formData.append("release", release);
        formData.append("director", director);
        formData.append("country", country);
        formData.append("movieimage", image);

        setMoviename("");
        setGenre("");
        setRating("");
        setRelease("");
        setDirector("");
        setCountry("");

        axios.post("/api/movies", formData).then((res) => {
            console.log(res)
        }).catch((err) => {
            console.log(err);
        })
    }


    return (
        <div>

            <form className='form' method='POST' encType='multipart/form-data' onSubmit={changeOnClick}>
                <input
                    placeholder="moviename"
                    name='moviename'
                    type="text"
                    value={moviename}
                    onChange={(e) => setMoviename(e.target.value)}
                    className="form-control" />
                <input
                    placeholder="genre"
                    name='genre'
                    value={genre}
                    type="text"
                    onChange={(e) => setGenre(e.target.value)}
                    className="form-control" />
                <input
                    placeholder="rating"
                    name='rating'
                    value={rating}
                    type="number"
                    onChange={(e) => setRating(e.target.value)}
                    className="form-control" />
                <input
                    placeholder="release"
                    name='release'
                    type="text"
                    value={release}
                    onChange={(e) => setRelease(e.target.value)}
                    className="form-control" />
                     <input
                    placeholder="director"
                    name='director'
                    type="text"
                    value={director}
                    onChange={(e) => setDirector(e.target.value)}
                    className="form-control" />
                     <input
                    placeholder="country"
                    name='country'
                    type="text"
                    value={country}
                    onChange={(e) => setCountry(e.target.value)}
                    className="form-control" />
                <input type="file"
                    filename="movieimage"
                    className="form-control-file"
                    onChange={onChangeImage}
                />
                <button className='button' type="submit">List Movie</button>
            </form>

            {movielist.map((movies)=>{
                return(
                    <div>
                        {movies.moviename}
                        genre
                        rating
                        release
                        director}
                        country

                    </div>
                )
            })}
        </div>
    )
}

export default Home
