import React, { useState, useEffect } from 'react'
import axios from "axios"
import { Input } from 'antd';
import {
    MenuFoldOutlined,
    MenuUnfoldOutlined,
    UploadOutlined,
    UserOutlined,
    VideoCameraOutlined,
} from '@ant-design/icons';
import { Layout, Menu, theme } from 'antd';
const { Header, Sider, Content } = Layout;

function Home() {
    const [moviename, setMoviename] = useState("");
    const [genre, setGenre] = useState("");
    const [rating, setRating] = useState("");
    const [release, setRelease] = useState("");
    const [director, setDirector] = useState("");
    const [country, setCountry] = useState("");
    const [image, setImage] = useState("")
    const [movielist, setMovielist] = useState([])
    const [collapsed, setCollapsed] = useState(false);

    const {
        token: { colorBgContainer },
    } = theme.useToken();


    useEffect(() => {
        axios.get('/api/movies').then((res) => {
            setMovielist(res.data)
            console.log(movielist)
        })
    }, [])


    const onChangeImage = (e) => {
        setImage(e.target.files[0]);
    }

    const changeOnClick = (e) => {
        e.preventDefault();

        const formData = new FormData();

        formData.append("moviename", moviename);
        formData.append("genre", genre);
        formData.append("rating", rating);
        formData.append("release", release);
        formData.append("director", director);
        formData.append("country", country);
        formData.append("movieimage", image);

        setMoviename("");
        setGenre("");
        setRating("");
        setRelease("");
        setDirector("");
        setCountry("");

        axios.post("/api/movies", formData).then((res) => {
            console.log(res)
        }).catch((err) => {
            console.log(err);
        })
    }

    const DeleteMovie = (id) => {
        axios.delete(`/api/movies/${id}`).then((res) => {
            window.location.reload();
        })
    }

    return (
        <div id='home'>
            <Layout>
      <Sider trigger={null} collapsible collapsed={collapsed}>
        <div className="logo" />
        <Menu
          theme="dark"
          mode="inline"
          defaultSelectedKeys={['1']}
          items={[
            {
              key: '1',
              icon: <UserOutlined />,
              label: 'nav 1',
            },
            {
              key: '2',
              icon: <VideoCameraOutlined />,
              label: 'nav 2',
            },
            {
              key: '3',
              icon: <UploadOutlined />,
              label: 'nav 3',
            },
          ]}
        />
      </Sider>
      <Layout className="site-layout">
        <Header
          style={{
            padding: 0,
            background: colorBgContainer,
          }}
        >
          {React.createElement(collapsed ? MenuUnfoldOutlined : MenuFoldOutlined, {
            className: 'trigger',
            onClick: () => setCollapsed(!collapsed),
          })}
        </Header>
        <Content
          style={{
            margin: '24px 16px',
            padding: 24,
            minHeight: 280,
            background: colorBgContainer,
          }}
        >
          Content
        </Content>
      </Layout>
    </Layout>

            <form className='form' method='POST' encType='multipart/form-data' onSubmit={changeOnClick}>
                <label htmlFor="">moviename</label>
                <Input
                    placeholder="moviename"
                    name='moviename'
                    type="text"
                    value={moviename}
                    onChange={(e) => setMoviename(e.target.value)}
                    className="form-control" />
                <label htmlFor="">genre</label>
                <Input
                    placeholder="genre"
                    name='genre'
                    value={genre}
                    type="text"
                    onChange={(e) => setGenre(e.target.value)}
                    className="form-control" />
                <label htmlFor="">rating</label>
                <Input
                    placeholder="rating"
                    name='rating'
                    value={rating}
                    type="text"
                    onChange={(e) => setRating(e.target.value)}
                    className="form-control" />
                <label htmlFor="">release</label>
                <Input
                    placeholder="release"
                    name='release'
                    type="text"
                    value={release}
                    onChange={(e) => setRelease(e.target.value)}
                    className="form-control" />
                <label htmlFor="">director</label>
                <Input
                    placeholder="director"
                    name='director'
                    type="text"
                    value={director}
                    onChange={(e) => setDirector(e.target.value)}
                    className="form-control" />
                <label htmlFor="">country</label>
                <Input
                    placeholder="country"
                    name='country'
                    type="text"
                    value={country}
                    onChange={(e) => setCountry(e.target.value)}
                    className="form-control" />
                <input type="file"
                    filename="movieimage"
                    className="form-control-file"
                    onChange={onChangeImage}
                />
                <button className='button' type="submit">List Movie</button>
            </form>

            {movielist.map((movies) => {
                return (
                    <div>
                        <img src={`uploads/${movies.image}`} className="image" alt='' />
                        {movies.moviename}
                        {movies.genre}
                        {movies.rating}
                        {movies.release}
                        {movies.director}
                        {movies.country}
                        <button onClick={() => DeleteMovie(movies._id)}> Delete</button>





                    </div>
                )
            })}
        </div>
    )
}

export default Home
