import React, { useEffect, useState } from 'react'
import axios from "axios";
import img1 from "../Images/5172658.jpg"
import "./CSS/Profile.css"
import { UploadOutlined } from '@ant-design/icons';
import { Button, message, Upload } from 'antd';


function Profile() {
    const local = localStorage.getItem('username');
    const [username, setUsername] = useState(local)
 const [list, setList] = useState("")
 const [image, setImage] = useState("");

    
 const onChangeImage = (e) => {
    setImage(e.target.files[0]);
}

const changeOnClick = (e) => {
    e.preventDefault();

    const formData = new FormData();

    formData.append("name", username);
    formData.append("fileimage", image);

    setName("");
    setPrice("");
    setDisc("");
    setCategory("")

    axios.post("/api/file", formData).then((res) => {
        console.log(res)
        setList([...list, {
            name,
            price,
            disc,
            category,

        }])
    }).catch((err) => {
        console.log(err);
    })
}

    useEffect(() => {
        axios.post("/api/Login/user", {
            username,
        }).then((res) => {
           setList(res.data)
       
        })
    }, [])

    return (
        <div>
            <img className='box' src={img1} alt="" ></img>
            <div className='box2'>
                <div className='box3'>
                    <div className='left'>

                        <img className='imagecircle' src="" alt="" />
                        <form >
      <input type="file" onChange={onChange}/>
      <button onClick={() => update(list._id)} type="submit">Update</button>
    </form>
      
  
                        <div className='userdetails'>

                            <div className="username">Username :{list.username}</div>
                            <div className="email">Email :   {list.email}</div>
                        </div>
                    </div>
                    <div className='right'>
                        <p className='profile'> Profile</p>
                        <div className="buttonclas"><Button>Add Payment Method</Button></div>
                        
                    </div>
                </div>
            </div>

        </div>
    )
}

export default Profile
