import React, { useEffect, useState } from 'react'
import axios from "axios";
import img1 from "../Images/5172658.jpg"
import "./CSS/Profile.css"
import { UploadOutlined } from '@ant-design/icons';
import { Button, message, Upload } from 'antd';


function Profile() {
    const local = localStorage.getItem('username');
    const [username, setUsername] = useState(local)
    const [list, setList] = useState("")
    const [image, setImage] = useState("");


    const onChangeImage = (e) => {
        setImage(e.target.files[0]);
    }

    const changeOnClick = (e) => {
        e.preventDefault();

        const formData = new FormData();

        formData.append("name", username);
        formData.append("image", image);

        setUsername("");

        axios.post("http://localhost:5000/api/Login/imagesave", formData).then((res) => {
            console.log(res)
         
        }).catch((err) => {
            console.log(err);
        })
    }

    useEffect(() => {
        axios.post("/api/Login/user", {
            username,
        }).then((res) => {
            setList(res.data)

        })
    }, [])

    return (
        <div>
            <img className='box' src={img1} alt="" ></img>
            <div className='box2'>
                <div className='box3'>
                    <div className='left'>

                        <img className='imagecircle' src="" alt="" />



                        <div className='userdetails'>

                            <div className="username">Username :{list.username}</div>
                            <div className="email">Email :   {list.email}</div>
                        </div>
                    </div>
                    <div className='right'>
                        <p className='profile'> Profile</p>
                        <div className="buttonclas"><Button>Add Payment Method</Button></div>
                        <form className='form' method='POST' encType='multipart/form-data' onSubmit={changeOnClick}>
                            <input
                                placeholder="Name"
                                type="text"
                                
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                className="form-control" />



                            <input type="file"
                                filename="image"
                                className="form-control-file"
                                onChange={onChangeImage}
                            />
                            <button className='button' type="submit">Add Product</button>
                        </form>

                    </div>
                </div>
            </div>

        </div>
    )
}

export default Profile
