import React,{useState,useEffect} from 'react'
import { Card } from 'antd';
import axios from "axios"
import { StarFilled } from '@ant-design/icons';
import { Input } from 'antd';

const { Meta } = Card;

function Movies() {
const [movielist, setMovielist] = useState([]);

useEffect(() => {
 axios.get('/api/movies').then((res)=>{
    setMovielist(res.data);
 })
}, [])


  return (
    <div>
        <div className='search'><Input placeholder="Search" /></div>

<div className='Card'>
    
        {movielist.map((data) => {
          return (
            <a className='movieroute' href={`/Movie/${data._id}`}>
              <Card
              className='card1'
              hoverable
              cover={<img className='cardimage' alt="example" src={`uploads/${data.image}`}/>}
            ><div > <Meta title={data.moviename}  className="title"/>
            <div className='meta'>
            <div>Year : {data.release}</div>  
           <div className="rating"><StarFilled /> {data.rating}</div> 
            </div>
         
            </div>
             
            </Card>
            </a>
            
          )
        })}
      </div>
    </div>
  )
}

export default Movies
