import React, { useEffect, useState } from 'react'
import axios from "axios";
import img1 from "../Images/5172658.jpg"
import "./CSS/Profile.css"
import { UploadOutlined } from '@ant-design/icons';
import { Button, message, Upload } from 'antd';


function Profile() {
    const local = localStorage.getItem('username');
    const [username, setUsername] = useState(local)
   const [email, setEmail] = useState("")
    const [image, setImage] = useState(null);
    const [id, setId] = useState()
    

    const onChange = (e) => {
        setImage(e.target.files[0]);
      };
    
      const update = (e,id) => {
        e.preventDefault();
        console.log(id)
       
      };


    useEffect(() => {
        axios.post("/api/Login/user", {
            username,
        }).then((res) => {
           setEmail(res.data.email)
           setId(res.data._id)
        })
    }, [])

    return (
        <div>
            <img className='box' src={img1} alt="" ></img>
            <div className='box2'>
                <div className='box3'>
                    <div className='left'>

                        <img className='imagecircle' src="" alt="" />

      <input type="file" onChange={onChange} />
      <button onClick={() => update(id._id)}>Delete</button>
  
                        <div className='userdetails'>

                            <div className="username">Username :{username}</div>
                            <div className="email">Email :   {email}</div>
                        </div>
                    </div>
                    <div className='right'>
                        <p className='profile'> Profile</p>
                        <div className="buttonclas"><Button>Add Payment Method</Button></div>
                        
                    </div>
                </div>
            </div>

        </div>
    )
}

export default Profile
