import React,{useState}from 'react'
import { LockOutlined, UserOutlined } from '@ant-design/icons';
import { Button, Checkbox, Form, Input } from 'antd';
import "./CSS/Login.css"
import img1 from "../Images/best-fall-movies-1659459329.jpg"
import axios from "axios"

function Login() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");



    const onFinish = (values) => {
        console.log('Received values of form: ', values);
    };

    const OnSubmit = ()=>{
        Axios.post("/api/Login/",{
            username,
            password
        })
    }

    return (
        <div id='form'>
            <div>
            <p className='p'>LOGIN HERE</p>
            <Form
                name="normal_login"
                className="login-form"
                initialValues={{
                    remember: true,
                }}
                onFinish={onFinish}
            >
                <Form.Item
                    name="username"
                    rules={[
                        {
                            required: true,
                            message: 'Please input your Username!',
                        },
                    ]}
                >
                    <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Username" onChange={(e)=>setUsername(e.target.value)}/>
                </Form.Item>
                <Form.Item
                    name="password"
                    rules={[
                        {
                            required: true,
                            message: 'Please input your Password!',
                        },
                    ]}
                >
                    <Input
                        prefix={<LockOutlined className="site-form-item-icon" />}
                        type="password"
                        placeholder="Password"
                        onChange={(e)=>setPassword(e.target.password)}
                    />
                </Form.Item>
                <Form.Item>
                    <Form.Item name="remember" valuePropName="checked" noStyle>
                        <Checkbox>Remember me</Checkbox>
                    </Form.Item>

                    <a className="login-form-forgot" href="">
                        Forgot password
                    </a>
                </Form.Item>

                <Form.Item>
                    <Button type="primary" htmlType="submit" className="login-form-button" onClick={OnSubmit}>
                        Log in
                    </Button>
                    
                </Form.Item>
            </Form>
            </div>
            

            <dir>
                <img src={img1} alt="" />
            </dir>
        </div>
    )
}

export default Login
